import { useTranslation } from 'react-i18next';

const STRAPI_BASE_URL = process.env.REACT_APP_STRAPI_URL || 'http://localhost:1337';

export interface StrapiArticle {
  id: number;
  attributes: {
    title: string;
    slug: string;
    content: string;
    excerpt: string;
    readingTime: number;
    featured: boolean;
    publishDate: string;
    category: {
      data: {
        id: number;
        attributes: {
          name: string;
          slug: string;
          color: string;
          icon: string;
        };
      };
    };
    author: {
      data: {
        id: number;
        attributes: {
          name: string;
          credentials: string;
          specialty: string;
          bio: string;
        };
      };
    };
    tags: {
      data: Array<{
        id: number;
        attributes: {
          name: string;
          slug: string;
        };
      }>;
    };
    seoTitle?: string;
    seoDescription?: string;
    seoKeywords?: string;
    locale: string;
    localizations: {
      data: Array<{
        id: number;
        attributes: {
          locale: string;
        };
      }>;
    };
  };
}

export interface StrapiCategory {
  id: number;
  attributes: {
    name: string;
    slug: string;
    description?: string;
    color: string;
    icon: string;
    displayOrder: number;
    locale: string;
  };
}

export interface StrapiAuthor {
  id: number;
  attributes: {
    name: string;
    credentials: string;
    specialty: string;
    bio: string;
    languages: string[];
    locale: string;
  };
}

export interface BlogArticle {
  id: string;
  category: string;
  title: string;
  preview: string;
  content: string;
  readingTime: number;
  publishDate: string;
  author?: string;
  tags?: string[];
  seo?: {
    title?: string;
    description?: string;
    keywords?: string;
  };
}

class StrapiApiService {
  private baseUrl: string;

  constructor() {
    this.baseUrl = STRAPI_BASE_URL;
  }

  private async fetchFromStrapi(endpoint: string, params: Record<string, any> = {}) {
    const searchParams = new URLSearchParams();

    Object.entries(params).forEach(([key, value]) => {
      if (value !== undefined && value !== null) {
        if (typeof value === 'object') {
          searchParams.append(key, JSON.stringify(value));
        } else {
          searchParams.append(key, value.toString());
        }
      }
    });

    const url = `${this.baseUrl}/api/${endpoint}${searchParams.toString() ? `?${searchParams.toString()}` : ''}`;

    try {
      const response = await fetch(url);
      if (!response.ok) {
        throw new Error(`HTTP error! status: ${response.status}`);
      }
      return await response.json();
    } catch (error) {
      console.error('Strapi API Error:', error);
      throw error;
    }
  }

  async getArticles(params: {
    locale?: string;
    search?: string;
    category?: string;
    featured?: boolean;
    page?: number;
    pageSize?: number;
    sort?: string;
  } = {}): Promise<{ articles: BlogArticle[]; total: number; page: number; pageSize: number }> {
    const {
      locale = 'en',
      search,
      category,
      featured,
      page = 1,
      pageSize = 10,
      sort = 'publishDate:desc'
    } = params;

    const filters: any = {
      // Temporarily show all articles (including drafts) in development
      // TODO: Change back to publishedAt: { $notNull: true } for production
    };

    if (search) {
      filters.$or = [
        { title: { $containsi: search } },
        { content: { $containsi: search } },
        { excerpt: { $containsi: search } }
      ];
    }

    if (category && category !== 'all') {
      filters.category = {
        slug: { $eq: category }
      };
    }

    if (featured !== undefined) {
      filters.featured = { $eq: featured };
    }

    const queryParams = {
      locale,
      filters,
      sort,
      populate: {
        category: true,
        author: true,
        tags: true,
        localizations: true
      },
      pagination: {
        page,
        pageSize
      }
    };

    try {
      const response = await this.fetchFromStrapi('articles', queryParams);
      const articles: BlogArticle[] = response.data.map(this.transformArticle);

      return {
        articles,
        total: response.meta.pagination.total,
        page: response.meta.pagination.page,
        pageSize: response.meta.pagination.pageSize
      };
    } catch (error) {
      console.error('Error fetching articles:', error);
      return { articles: [], total: 0, page: 1, pageSize: 10 };
    }
  }

  async getArticleBySlug(slug: string, locale: string = 'en'): Promise<BlogArticle | null> {
    try {
      const queryParams = {
        locale,
        filters: {
          slug: { $eq: slug }
          // Temporarily removed publishedAt filter for development
        },
        populate: {
          category: true,
          author: true,
          tags: true,
          localizations: true
        }
      };

      const response = await this.fetchFromStrapi('articles', queryParams);

      if (response.data && response.data.length > 0) {
        return this.transformArticle(response.data[0]);
      }

      return null;
    } catch (error) {
      console.error('Error fetching article by slug:', error);
      return null;
    }
  }

  async getCategories(locale: string = 'en'): Promise<{ key: string; label: string; color: string; icon: string }[]> {
    try {
      const queryParams = {
        locale,
        filters: {
          publishedAt: { $notNull: true }
        },
        sort: 'displayOrder:asc'
      };

      const response = await this.fetchFromStrapi('categories', queryParams);

      const categories = response.data.map((category: StrapiCategory) => ({
        key: category.attributes.slug,
        label: category.attributes.name,
        color: category.attributes.color,
        icon: category.attributes.icon
      }));

      // Add "all" category at the beginning
      return [
        { key: 'all', label: 'All Categories', color: '#666', icon: 'ðŸ“š' },
        ...categories
      ];
    } catch (error) {
      console.error('Error fetching categories:', error);
      return [{ key: 'all', label: 'All Categories', color: '#666', icon: 'ðŸ“š' }];
    }
  }

  async getFeaturedArticles(locale: string = 'en', limit: number = 5): Promise<BlogArticle[]> {
    const result = await this.getArticles({
      locale,
      featured: true,
      pageSize: limit,
      sort: 'publishDate:desc'
    });

    return result.articles;
  }

  private transformArticle(strapiArticle: StrapiArticle): BlogArticle {
    const { attributes } = strapiArticle;

    return {
      id: strapiArticle.id.toString(),
      category: attributes.category?.data?.attributes?.name || 'Uncategorized',
      title: attributes.title,
      preview: attributes.excerpt,
      content: attributes.content,
      readingTime: attributes.readingTime,
      publishDate: attributes.publishDate,
      author: attributes.author?.data?.attributes?.name,
      tags: attributes.tags?.data?.map(tag => tag.attributes.name) || [],
      seo: {
        title: attributes.seoTitle,
        description: attributes.seoDescription,
        keywords: attributes.seoKeywords
      }
    };
  }
}

// Create a singleton instance
export const strapiApi = new StrapiApiService();

// Hook for using Strapi API with current locale
export const useStrapiApi = () => {
  const { i18n } = useTranslation();
  const currentLocale = i18n.language === 'es' ? 'es' : 'en';

  return {
    getArticles: (params = {}) => strapiApi.getArticles({ ...params, locale: currentLocale }),
    getArticleBySlug: (slug: string) => strapiApi.getArticleBySlug(slug, currentLocale),
    getCategories: () => strapiApi.getCategories(currentLocale),
    getFeaturedArticles: (limit?: number) => strapiApi.getFeaturedArticles(currentLocale, limit),
  };
};
